mapper.select:
SELECT E.EMP_ID AS EMP_ID, E.EMPNO AS EMPNO, E.EMP_FLNM AS EMP_FLNM, E.EMP_TY_CD AS EMP_TY_CD, C.CD_NM AS JBPS_NM, MAX(EH.JNCMP_YMD) AS JNCMP_YMD, -- 최신 JNCMP_YMD로 수정 GROUP_CONCAT(D.DEPT_NM) AS DEPT_ID, E.TELNO AS TELNO, E.EML AS EML, C2.CD_NM AS HDOF_STTS_NM, COUNT(*) OVER () AS TOTAL_ITEMS, C3.CD_NM AS BANK_NM, E.ACTNO AS ACTNO, C.CD_VALUE AS JBPS_CD, C3.CD_VALUE AS BANK_CD, C2.CD_VALUE AS HDOF_STTS_CD FROM EMP E LEFT OUTER JOIN CD C ON C.CD_VALUE = E.JBPS_CD LEFT OUTER JOIN DEPT_HNF DF ON E.EMP_ID = DF.EMP_ID LEFT OUTER JOIN DEPT D ON D.DEPT_ID = DF.DEPT_ID LEFT OUTER JOIN CD C2 ON C2.CD_VALUE = E.HDOF_STTS_CD LEFT OUTER JOIN CD C3 ON C3.CD_VALUE = E.BANK_CD LEFT OUTER JOIN EMP_HIST EH ON EH.EMP_ID = E.EMP_ID WHERE 1=1
AND E.EMPNO LIKE CONCAT('%', #{empno}, '%')
AND E.EMP_FLNM LIKE CONCAT('%', #{empFlnm}, '%')
AND E.JBPS_CD = #{jbpsNm}
AND E.TELNO LIKE CONCAT('%', #{telNo}, '%')
AND D.DEPT_ID = #{deptId}
AND C2.CD_VALUE = #{hdofSttsNm}
GROUP BY E.EMP_ID, E.EMPNO, E.EMP_FLNM, E.EMP_TY_CD, C.CD_NM, E.TELNO, E.EML, C2.CD_NM, C3.CD_NM, E.ACTNO, C.CD_VALUE, C3.CD_VALUE, C2.CD_VALUE ORDER BY E.JBPS_CD ASC

WITH EMP_HIST_RANK AS ( SELECT EMP_ID, JBPS_CD, EMP_HIST_SN, ROW_NUMBER() OVER (PARTITION BY EMP_ID ORDER BY REG_DT) AS RowNum , REG_DT, EMP_INFO_CHG_ODR, REG_EMP_ID FROM EMP_HIST ) SELECT A.EMP_ID, B.EMP_INFO_CHG_ODR, B.EMP_HIST_SN, A.JBPS_CD AS JBPS_CD, C.CD_NM AS JBPS_NM, DATE_FORMAT(B.REG_DT,'%Y-%m-%d') AS REG_DT, D.EMPNO AS REG_EMPNO FROM EMP_HIST_RANK A JOIN EMP_HIST_RANK B ON A.EMP_ID = B.EMP_ID AND A.RowNum = B.RowNum - 1 LEFT OUTER JOIN CD C ON C.CD_VALUE =A.JBPS_CD LEFT OUTER JOIN EMP D ON D.EMP_ID=A.REG_EMP_ID WHERE 1=1 AND A.JBPS_CD <> B.JBPS_CD
AND A.EMP_ID = #{empId}
ORDER BY EMP_HIST_SN DESC;
